function writeResultsToExelAll(setup, rootDir,projType,exelFile,costYear,Population,Area,nameReg,calc)
%
% FUNCTION writeResultsToExelAll(setup, rootDir, projType, exelFile, costYear, Population, Area, OvernightFlag, SCFlag, GasFlag, DesalinationFlag, Capex_Rehab, OnlyFlag, nameReg, calc)
%
% Writes the results of a scenario to an Excel file for further analysis or reporting.
%
% INPUT:
%            setup: Structure that contains all necessary settings and data for processing
%            rootDir: Root directory for saving the Excel file
%            projType: Project type or scenario category
%            exelFile: Name of the Excel file to write to
%            costYear: Year to which costs are adjusted
%            Population: Population data
%            Area: Area data
%            OvernightFlag: Boolean flag for overnight scenario
%            SCFlag: Boolean flag for prosumers
%            GasFlag: Boolean flag indicating use of gas
%            DesalinationFlag: Boolean flag for desalination inclusion
%            Capex_Rehab: Capital expenditure for rehabilitation
%            OnlyFlag: Boolean or condition to restrict results
%            nameReg: ADD!!!!!!!!!!!!!!!!
%            calc: ADD!!!!!!!!!!!!!!!!
%
% OUTPUT:
%            -
%
%Dmitrii Bogdanov
%last change 19.01.2017


scriptVersion = 5.2;

%% Get project name

pName = [projType];

if setup.MacroMc
    pName = [pName '_Mc'];
end

if setup.MacroReg
    pName = [pName '_R'];
end

if setup.OvernightFlag
    pName = [pName '_Overnight'];
end

if setup.SC.Flag
    pName = [pName '_EL_SC'];
end

if setup.Heat.Flag
    pName = [pName '_HE'];
end

if setup.Mobility
    pName = [pName '_TR'];
end

if setup.IndustryFlag
    pName = [pName '_IND'];
end

if setup.GasFlag
    pName = [pName '_GAS'];
end

if setup.DesalinationFlag
    pName = [pName '_DES'];
end

if setup.Only.Flag
    pName = [pName '_Only'];
end

pName = [pName '_' num2str(costYear)];



load([rootDir filesep 'projects' filesep 'resultsCalculated' filesep 'resultsCalculated_' pName '_' nameReg]);

pName = [projType];

if setup.MacroMc
    pName = [pName '_Mc'];
end

if setup.MacroReg
    pName = [pName '_R'];
end

if setup.OvernightFlag
    pName = [pName '_O'];
end

if setup.SC.Flag
    pName = [pName '_EL_SC'];
end

if setup.Heat.Flag
    pName = [pName '_HE'];
end

if setup.Mobility
    pName = [pName '_TR'];
end

if setup.GasFlag
    pName = [pName '_GAS'];
end

if setup.DesalinationFlag
    pName = [pName '_DES'];
end

if setup.Only.Flag
    pName = [pName '_Only'];
end

pName = [pName '_' num2str(costYear)];
name = pName;
Capex_Rehab = setup.Capex_Rehab;
capacitiesREN = 0;

capacitiesREN = results.OPT_SIZE_RPVO+...
    results.OPT_SIZE_RPVA+...
    results.OPT_SIZE_RPBO+...
    results.OPT_SIZE_RPVF+...
    results.OPT_SIZE_RPBV+...
    results.OPT_SIZE_RPBA+...
    results.OPT_SIZE_RWIN+...
    results.OPT_SIZE_RWIO+...
    results.OPT_SIZE_ROWI+...
    results.OPT_SIZE_RRRI+...
    results.OPT_SIZE_HDAM+...
    results.OPT_SIZE_TSTU+...
    results.OPT_SIZE_TBPP+...
    results.OPT_SIZE_TMSW+...
    results.OPT_SIZE_TCWC+...
    results.OPT_SIZE_TCHP+...
    results.OPT_SIZE_TCBP+...
    results.OPT_SIZE_TCBC+...
    results.OPT_SIZE_TGEO;


capacitiesFOS = 0;

capacitiesFOS = results.OPT_SIZE_TCCG+...
    results.OPT_SIZE_TOCG+...
    results.OPT_SIZE_TGCS+...
    results.OPT_SIZE_THPP+...
    results.OPT_SIZE_THCS+...
    results.OPT_SIZE_TICG+...
    results.OPT_SIZE_TICM+...
    results.OPT_SIZE_TNUC+...
    results.OPT_SIZE_TCNG+...
    results.OPT_SIZE_TCCO+...
    results.OPT_SIZE_TCOI;

%% Primary gen shares for Power!

allTransf = logical(activeElements.activeElTransformer + activeElements.activeCHPTransformer + activeElements.activeHeatTransformer  + activeElements.activeGasTransformer);
% reshaping of data structure
for h=1:length(systemParams.IndexFuels)
    TMPefficiencyTransf(1,[1:length(systemParams.IndexID(allTransf))],h) = systemParams.EtaTrans(allTransf,h)';
end
efficiencyTransf = repmat(TMPefficiencyTransf,[length(systemParams.IndexNodes) 1 1]);
efficiencyTransf(isnan(efficiencyTransf)) = 0;
IDT = systemParams.IndexID(allTransf);
IDF = systemParams.IndexFuels;

nuc = (sum(results.TNUC_EL));

sw = (sum(results.RCSP_HE*0.43))+(sum(results.RPVA_EL)+sum(results.RPVO_EL)+sum(results.RPBA_EL)+sum(results.RPBO_EL)+sum(results.RPVF_EL)+sum(results.RPBV_EL))+(sum(results.RWIN_EL))+(sum(results.RWIO_EL))+(sum(results.ROWI_EL))+(sum(resultsSC.RPVR_EL+resultsSC.RPVC_EL+resultsSC.RPVI_EL));
solarS = (sum(results.RCSP_HE*0.43))+(sum(results.RPVA_EL)+sum(results.RPVO_EL)+sum(results.RPBA_EL)+sum(results.RPBO_EL)+sum(results.RPVF_EL)+sum(results.RPBV_EL))+(sum(resultsSC.RPVR_EL+resultsSC.RPVC_EL+resultsSC.RPVI_EL));
windS = (sum(results.RWIN_EL))+(sum(results.RWIO_EL))+(sum(results.ROWI_EL));

re = (sum(results.RWAV_EL))+(sum(results.RRRI_EL))+(sum(results.HDAM_EL))+(sum(results.TGEO_EL))+(sum(results.TBPP_EL))+(sum(results.TCBP_EL))+(sum(results.TCHP_EL))+(sum(results.TMSW_EL))+(sum(results.TBGU_GA))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FGAS'));
hydroS = (sum(results.RRRI_EL))+(sum(results.HDAM_EL));
bioS = (sum(results.TBPP_EL))+(sum(results.TCBP_EL))+(sum(results.TCHP_EL))+(sum(results.TMSW_EL))+(sum(results.TBGU_GA))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FGAS'));
geoS = (sum(results.TGEO_EL));

thermRE = ((sum(results.TCCG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FGAS'))+(sum(results.TOCG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FGAS'))+(sum(results.TGCS_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FGAS'))+(sum(results.TCNG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FGAS'))+(sum(results.TICM_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FGAS'))).*((shareOfGasIndSNGimp)')+...
    ((sum(results.HY_TCCG))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FHYG'))+(sum(results.HY_TOCG))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FHYG'))+(sum(results.HY_TGCS))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FHYG'))+(sum(results.HY_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FHYG'))+(sum(results.HY_TICM))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FHYG'))).*((shareOfHyImpor)');

fCoal = (sum(results.THPP_EL))+(sum(results.THCS_EL))+(sum(results.TCCO_EL));
fOil = (sum(results.TICG_EL))+(sum(results.TCOI_EL))+...
    (sum(results.RPET_TCCG))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TOCG))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TGCS))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TICM))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FLIQ'));
fGas = (sum(results.TCCG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FGAS'))+(sum(results.TOCG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FGAS'))+(sum(results.TGCS_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FGAS'))+(sum(results.TCNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FGAS'))+(sum(results.TICM_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FGAS'));


foss = (sum(results.THPP_EL))+(sum(results.THCS_EL))+(sum(results.TICG_EL))+(sum(results.TCOI_EL))+(sum(results.TCCO_EL))+...
    (sum(results.RPET_TCCG))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TOCG))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TGCS))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FLIQ'))+(sum(results.RPET_TICM))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FLIQ'))+...
    (sum(results.TCCG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCCG'),ismember(IDF,'FGAS'))+(sum(results.TOCG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TOCG'),ismember(IDF,'FGAS'))+(sum(results.TGCS_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TGCS'),ismember(IDF,'FGAS'))+(sum(results.TCNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FGAS'))+(sum(results.TICM_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TICM'),ismember(IDF,'FGAS'));

fossHe = (sum(results.TCNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+(sum(results.RPET_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+(sum(results.TCOI_HE))+(sum(results.TCCO_HE))+...
    (sum(results.TDNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TDNG'),ismember(IDF,'FGAS')) + (sum(results.TDOI_HE))+(sum(results.TDCO_HE))+...
    (sum(results.LHIN_GAS_FOS))+(sum(results.RPET_LHIN))+(sum(results.RHAR_LHIN));
fossHeGas = ...
    (sum(results.TCNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+...
    (sum(results.TDNG_GAS_FOS))*efficiencyTransf(1,ismember(IDT,'TDNG'),ismember(IDF,'FGAS')) + ...
    (sum(results.LHIN_GAS_FOS))+(sum(resultsSC.THNG_HE)).*(sum(results.Pros_GAS_FOS)./(sum(results.Pros_GAS_FOS+results.Pros_GAS_REN)));
fossHeOil = (sum(results.RPET_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+(sum(results.TCOI_HE))+...
    (sum(results.TDOI_HE))+(sum(results.RPET_LHIN))+(sum(resultsSC.THOI_HE));
fossHeCoal = (sum(results.TCCO_HE))+(sum(results.TDCO_HE))+(sum(results.RHAR_LHIN));

otherHeEl = (sum(results.TDHP_HE))+(sum(results.TDHR_HE))+(sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE));
otherHeSolar = (sum(resultsSC.RRSH_HE))+(sum(results.RCSP_HE))+(sum(results.RDSH_HE));
otherHeGeo = (sum(results.TDGE_HE));
otherHeBio = ((sum(results.TCNG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+...
    (sum(results.TDNG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TDNG'),ismember(IDF,'FGAS')) + ...
    (sum(results.LHIN_GAS_REN))+(sum(resultsSC.THNG_HE)).*(sum(results.Pros_GAS_FOS)./(sum(results.Pros_GAS_FOS+results.Pros_GAS_REN)))).*((shareOfGasIndBME)')+...
    (sum(results.TCBP_HE))+(sum(results.TCHP_HE))+(sum(results.TMSW_HE))+...
    (sum(resultsSC.THBP_HE))+(sum(resultsSC.THBG_HE));

impHe = ((sum(results.TCNG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+...
    (sum(results.TDNG_GAS_REN))*efficiencyTransf(1,ismember(IDT,'TDNG'),ismember(IDF,'FGAS')) + ...
    (sum(results.LHIN_GAS_REN))+(sum(resultsSC.THNG_HE)).*(sum(results.Pros_GAS_FOS)./(sum(results.Pros_GAS_FOS+results.Pros_GAS_REN)))).*((shareOfGasIndSNGimp)')+...
    ((sum(results.HY_TCNG))*efficiencyTransf(1,ismember(IDT,'TCNG'),ismember(IDF,'FELE'))+...
    (sum(results.HY_LHIN))).*((shareOfHyImpor)');

totHe = (sum(results.TCNG_HE))+(sum(results.TCOI_HE))+(sum(results.TCCO_HE))+(sum(results.TCBP_HE))+(sum(results.TCHP_HE))+(sum(results.TMSW_HE))+...
    (sum(results.TDNG_HE))+(sum(results.TDOI_HE))+(sum(results.TDCO_HE))+(sum(results.TDBP_HE))+(sum(results.TDGE_HE))+(sum(results.TDHP_HE))+(sum(results.TDHR_HE))+(sum(results.RCSP_HE))+(sum(results.RDSH_HE))+...
    (sum(resultsSC.THNG_HE))+(sum(resultsSC.THOI_HE))+(sum(resultsSC.THBP_HE))+(sum(resultsSC.THBG_HE))+(sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE))+(sum(resultsSC.RRSH_HE))+...
    (sum(results.LHIN_GAS_FOS))+(sum(results.LHIN_GAS_REN))+(sum(results.RPET_LHIN))+(sum(results.RHAR_LHIN))+(sum(results.HY_LHIN));

elShareinIndivHeat = ((sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE)))./((sum(resultsSC.THNG_HE))+(sum(resultsSC.THOI_HE))+(sum(resultsSC.THBP_HE))+(sum(resultsSC.THBG_HE))+(sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE))+(sum(resultsSC.RRSH_HE)))
elShareinIndivHeatAv = sum((sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE)))./sum((sum(resultsSC.THNG_HE))+(sum(resultsSC.THOI_HE))+(sum(resultsSC.THBP_HE))+(sum(resultsSC.THBG_HE))+(sum(resultsSC.THHP_HE))+(sum(resultsSC.THHR_HE))+(sum(resultsSC.RRSH_HE)))

%%



data = [['Area'; '[1000km2]'; num2cell(single([systemParams.area'; sum(systemParams.area')] ))],...
    ['Population'; '[mil]'; num2cell(single([systemParams.population(:,yearNumb)/10^3; sum(systemParams.population(:,yearNumb)/10^3)] ))],...
    ['Final energy demand - All sectors';'[TWh]'; num2cell(single([(demand_origFinal+FinalEnergyDem_HeatDistr+FinalEnergyDem_HeatIndiv+FinalEnergyDem_Industry+FinalEnergyDem_Trans+FinalEnergyDem_Des)/10^9; sum(demand_origFinal+FinalEnergyDem_HeatDistr+FinalEnergyDem_HeatIndiv+FinalEnergyDem_Industry+FinalEnergyDem_Trans+FinalEnergyDem_Des)/10^9]))]...
    ['Final energy demand - Power sector';'[TWh]'; num2cell(single([demand_origFinal/10^9; sum(demand_origFinal)/10^9]))]...
    ['Final energy demand - Heat sector';'[TWh]'; num2cell(single([(FinalEnergyDem_HeatDistr+FinalEnergyDem_HeatIndiv)/10^9; sum(FinalEnergyDem_HeatDistr+FinalEnergyDem_HeatIndiv)/10^9]))]...
    ['Final energy demand - Transport sector';'[TWh]'; num2cell(single([FinalEnergyDem_Trans/10^9; sum(FinalEnergyDem_Trans)/10^9]))]...
    ['FED&NEU - Industry sector';'[TWh]'; num2cell(single([FinalEnergyDem_Industry/10^9; sum(FinalEnergyDem_Industry)/10^9]))]...
    ['Final energy demand - Desalination sector';'[TWh]'; num2cell(single([FinalEnergyDem_Des/10^9; sum(FinalEnergyDem_Des)/10^9]))]...
    ['Final energy demand - Exports';'[TWh]'; num2cell(single([FinalEnergyDem_Exports/10^9; sum(FinalEnergyDem_Exports)/10^9]))]...
    ['Final energy demand - CDR sector';'[TWh]'; num2cell(single([FinalEnergyDem_CDR/10^9; sum(FinalEnergyDem_CDR)/10^9]))]...
    ['Primary energy demand - All sectors';'[TWh]'; num2cell(single([(primary_energy_tot_Fossil+primary_energy_tot_Nuclear+primary_energy_tot_RE+FeedstockToSteel+FossilsToChemicals)/10^9; sum(primary_energy_tot_Fossil+primary_energy_tot_Nuclear+primary_energy_tot_RE+FeedstockToSteel+FossilsToChemicals)/10^9]))]...
    ['Primary energy demand - Fossil fuels';'[TWh]'; num2cell(single([(primary_energy_tot_Fossil+FossilsToChemicals)/10^9; sum(primary_energy_tot_Fossil+FossilsToChemicals)/10^9]))]...
    ['Primary energy demand - Nuclear';'[TWh]'; num2cell(single([primary_energy_tot_Nuclear/10^9; sum(primary_energy_tot_Nuclear)/10^9]))]...
    ['Primary energy demand - RE';'[TWh]'; num2cell(single([(primary_energy_tot_RE+FeedstockToSteel)/10^9; sum(primary_energy_tot_RE+FeedstockToSteel)/10^9]))]...
    ['Chemical industry fossils feedstock';'[TWh]'; num2cell(single([(FossilsToChemicals)/10^9; sum(FossilsToChemicals)/10^9]))]...
    ['Steel industry charcoal feedstock';'[TWh]'; num2cell(single([sum(results.CC_TISE+results.CC_TISH+results.CC_TISR,1)'/10^9; sum(sum(results.CC_TISE+results.CC_TISH+results.CC_TISR,1)')/10^9]))]...
    ['Enviromental heat (heat pumps)';'[TWh,th]';num2cell(single([(sum(resultsSC.THHP_HE+results.TDHP_HE-resultsSC.EL_THHP-results.EL_TDHP,1)./10^9)';sum(sum(resultsSC.THHP_HE+results.TDHP_HE-resultsSC.EL_THHP-results.EL_TDHP,1)./10^9)] ))] ...
    ['Primary energy demand - RE - PV';'[TWh]'; num2cell(single([(sum(resultsSC.RPVR_EL+resultsSC.RPVC_EL+resultsSC.RPVI_EL,1)'+sum(results.RPVO_EL,1)'+sum(results.RPVA_EL,1)'+sum(results.RPVF_EL,1)'+sum(results.RPBV_EL,1)'+sum(results.RPBO_EL,1)'+sum(results.RPBA_EL,1)')/10^9; sum(sum(resultsSC.RPVR_EL+resultsSC.RPVC_EL+resultsSC.RPVI_EL,1)'+sum(results.RPVO_EL,1)'+sum(results.RPVA_EL,1)'+sum(results.RPVF_EL,1)'+sum(results.RPBV_EL,1)'+sum(results.RPBO_EL,1)'+sum(results.RPBA_EL,1)')/10^9]))]...
    ['Primary energy demand - RE - Wind';'[TWh]'; num2cell(single([sum(results.RWIN_EL+results.RWIO_EL+results.ROWI_EL,1)'/10^9; sum(sum(results.RWIN_EL+results.RWIO_EL+results.ROWI_EL,1)')/10^9]))]...
    ['Primary energy demand - RE - Hydro';'[TWh]'; num2cell(single([(sum(results.RRRI_EL,1)'+sum(results.HDAM_EL,1)')/10^9; sum(sum(results.RRRI_EL,1)'+sum(results.HDAM_EL,1)')/10^9]))]...
    ['Primary energy demand - RE - Solar/Geothermal';'[TWh]'; num2cell(single([(sum(results.RGEO_HE,1)'+sum(results.RCSP_HE,1)'+sum(results.RDSH_HE,1)'+sum(resultsSC.RRSH_HE,1)')/10^9; sum(sum(results.RGEO_HE,1)'+sum(results.RCSP_HE,1)'+sum(results.RDSH_HE,1)'+sum(resultsSC.RRSH_HE,1)')/10^9]))]...
    ['Primary energy demand - RE - Bioenergy';'[TWh]'; num2cell(single([sum(results.RWOO_FU+results.RWWO_FU+results.RBMW_FU+results.RBGA_FU+results.RBDS_FU,1)'/10^9; sum(sum(results.RWOO_FU+results.RWWO_FU+results.RBMW_FU+results.RBGA_FU+results.RBDS_FU,1)')/10^9]))]...
    ['Primary energy demand - RE - Imported e-fuels - Oil';'[TWh]'; num2cell(single([(sum(results.importFT,1)')/10^9; sum(sum(results.importFT,1))/10^9]))]...
    ['Primary energy demand - RE - Imported e-fuels - Methane';'[TWh]'; num2cell(single([(sum(results.importLNG,1)')/10^9; sum(sum(results.importLNG,1))/10^9]))]...
    ['Primary energy demand - RE - Imported e-fuels - Hydrogen';'[TWh]'; num2cell(single([(sum(results.importH2,1)')/10^9; sum(sum(results.importH2,1))/10^9]))]...
    ['Primary energy demand - Fossil fuels - Coal (original)';'[TWh]'; num2cell(single([(sum(results.RHAR_FU-results.RHAR_OTHER,1)'+CoalToChemFeedstock)/10^9; sum(sum(results.RHAR_FU-results.RHAR_OTHER,1)'+CoalToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Fossil fuels - Coal (modified)';'[TWh]'; num2cell(single([(sum(results.RHAR_FU,1)'+CoalToChemFeedstock)/10^9; sum(sum(results.RHAR_FU,1)'+CoalToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Fossil fuels - Oil (original)';'[TWh]'; num2cell(single([(sum(results.RPET_FU-results.RPET_OTHER,1)'+OilToChemFeedstock)/10^9; sum(sum(results.RPET_FU-results.RPET_OTHER,1)'+OilToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Fossil fuels - Oil (modified)';'[TWh]'; num2cell(single([(sum(results.RPET_FU,1)'+OilToChemFeedstock)/10^9; sum(sum(results.RPET_FU,1)'+OilToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Fossil fuels - Methane (original)';'[TWh]'; num2cell(single([(sum(results.RNGA_FU-results.RNGA_OTHER,1)'+GasToChemFeedstock)/10^9; sum(sum(results.RNGA_FU-results.RNGA_OTHER,1)'+GasToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Fossil fuels - Methane (modified)';'[TWh]'; num2cell(single([(sum(results.RNGA_FU,1)'+GasToChemFeedstock)/10^9; sum(sum(results.RNGA_FU,1)'+GasToChemFeedstock)/10^9]))]...
    ['Primary energy demand - Local';'[TWh]'; num2cell(single([primary_energy_tot_Local/10^9; sum(primary_energy_tot_Local)/10^9]))]... ... Total electricity consumption
    ['Primary energy demand - Import';'[TWh]'; num2cell(single([primary_energy_tot_Import/10^9; sum(primary_energy_tot_Import)/10^9]))]... ... Total electricity consumption
    ['Total electricity demand - per capita (power sector)';'[MWh,el/capita]'; num2cell(single(round([demand_origTot/1000./(1000*systemParams.population(:,yearNumb));sum(demand_origTot/1000)/sum(1000*systemParams.population(:,yearNumb))],1)))]...
    ['Total final power consumption';'[TWh,el]'; num2cell(single([demand_origFinal/10^9; sum(demand_origFinal)/10^9]))]... ... Total electricity consumption
    ['Total electricity demand';'[TWh,el]';num2cell(single([(demand_origTot+(-el_cons_localpowerFromGrid-el_cons_localheatFromGrid)./((100-systemParams.AC_losses(:,yearNumb))/100))/10^9; sum(demand_origTot+(-el_cons_localpowerFromGrid-el_cons_localheatFromGrid)./((100-systemParams.AC_losses(:,yearNumb))/100))/10^9]))]... ... Total electricity demand
    ['Total electricity demand System';'[TWh,el]';num2cell(single([(demand_sys-el_cons_localheatFromGrid./((100-systemParams.AC_losses(:,yearNumb))/100))/10^9;sum(demand_sys-el_cons_localheatFromGrid./((100-systemParams.AC_losses(:,yearNumb))/100))/10^9]))] ...
